sys_script_include.script:
var getUserInformations = Class.create();
getUserInformations.prototype = Object.extendsObject(global.AbstractAjaxProcessor, {

    baseUserInfos: function() {
        var userName = this.getParameter("sysparm_user_name");
        var grUser = new GlideRecord('sys_user');
        grUser.addQuery('sys_id', userName);
        grUser.query();

        var arrResult = {};
        while (grUser.next()) {
            arrResult.submitter_email = grUser.email.toString();
            arrResult.submitter_contract = grUser.phone.toString();
        }

        return JSON.stringify(arrResult);
    },

    type: 'getUserInformations'
});
